import React from "react"
import { nanoid } from "nanoid"

export default function Question(props) {

    const [flag,setFlag] = React.useState(true)
    const [answerCheck, setAnswerCheck] = React.useState(false)

    // TOGGLE BUTTON BACKGROUND COLOR WHEN BTN CLICKED
    function toggleBtnBgColor(btn) {
        if(flag) {
            btn.target.classList.toggle("dark-bg-color")
            setFlag(false)
            if(btn.target.value == props.answers.correct_answer) {
                setAnswerCheck(true)
            }
        }
    }

    return (
        <>
            <div className="question" calcAnswer={answerCheck} answerCheck={props.answerCheck} key={props.index}>{props.question}</div>
                {props.answers.map((answer,index)=> {
                    return (
                        <button key={index} onClick={toggleBtnBgColor} className="answer-button">{answer}</button>
                    )
            })}
            <hr></hr>
        </>
    )
}
