import React from "react"
import { useDispatch } from "react-redux"
import { calculate } from "./score"
import { useSelector } from "react-redux"
import { initColor } from "./color"

export default function Question(props) {

    const dispatch = useDispatch()
    const color = useSelector((state) => state.color.value)
    const [btnClassNames, setBtnClassNames] = React.useState("answer-button") 
    const [flag, setFlag] = React.useState(true)

    React.useEffect(() => {
        setFlag(true)
    }, [props.answers])

    // TOGGLE BUTTON BACKGROUND COLOR WHEN BTN CLICKED
    function toggleBtnBgColor(btn, correctAnswer) {
        if(flag) {
            if(color === "light-bg-color") {
                setBtnClassNames("answer-button dark-bg-color")
            }
        }
    }

    return (
        <>
            <div className="question" key={props.index}>{props.question}</div>
                {props.answers.map((answer,index)=> {
                    return (
                        <button className={btnClassNames} key={index} value={answer} onClick={event => toggleBtnBgColor(event,props.correctAnswer)} >{answer}</button>
                    )
            })}
        </>
    )
}
