import React from "react"
import Question from "./Question"

export default function Quiz() {

    const [data, setData] = React.useState()

    // FETCH QUIZ DATA FROM API
    const fetchData = () => {
        fetch("https://opentdb.com/api.php?amount=5&type=multiple")
        .then((response) => response.json())
        .then((data) => setData(data.results))
    }

    React.useEffect(()=> {
        fetchData()
    },[])

    // SHUFFLE ALGORITHM TO USE LATER
    function shuffle(array) {
        let currentIndex = array.length,  randomIndex;

        // While there remain elements to shuffle.
        while (currentIndex != 0) {

          // Pick a remaining element.
          randomIndex = Math.floor(Math.random() * currentIndex);
          currentIndex--;

          // And swap it with the current element.
          [array[currentIndex], array[randomIndex]] = [
            array[randomIndex], array[currentIndex]];
        }

        return array;
    }

    const questionElements = data.map((question, index) => {
        // ANSWERS ARRAY AND SHUFFLE
        let answers = [...question.incorrect_answers,question.correct_answer]
        shuffle(answers)

        // TOGGLE BUTTON BACKGROUND COLOR WHEN BTN CLICKED
        function toggleBtnBgColor(btn) {
            btn.target.classList.toggle("dark-bg-color")
            btn.target.onclick= ""
            btn.target.setFlag(true)
        }

        return (
            <Question setFlag={setFlag} index={index} question={question.question} answers={answers} toggleBtnBgColor={toggleBtnBgColor} />
        )
    })

    return (
        <div className="question-container">
            {questionElements}
        </div>
    )

}
