import React from "react"
import { nanoid } from "nanoid"

export default function Quiz() {

    const [questions, setQuestions] = React.useState([])

    const fetchData = () => {
        fetch("https://opentdb.com/api.php?amount=5&type=multiple")
        .then((response) => response.json())
        .then((data) => setQuestions(data.results))
    }

    React.useEffect(()=> {
        fetchData()
    },[])

    function shuffle(array) {
        let currentIndex = array.length,  randomIndex;

        // While there remain elements to shuffle.
        while (currentIndex != 0) {

          // Pick a remaining element.
          randomIndex = Math.floor(Math.random() * currentIndex);
          currentIndex--;

          // And swap it with the current element.
          [array[currentIndex], array[randomIndex]] = [
            array[randomIndex], array[currentIndex]];
        }

        return array;
    }

    const questionElements = questions.map((question) => {

        // GET ANSWERS AND SHUFFLE
        let answers = question.incorrect_answers
        answers = [...answers, question.correct_answer]
        shuffle(answers)

        function btnClicked(btn) {
            console.log(btn.target)
            btn.target.style = {backgroundColor: "#D6DBF5"}
        }

        return (
            <>
                <div className="question" key={nanoid()}>{question.question}</div>
                {answers.map((answer)=> {
                    return (
                        <button key={nanoid()} onClick={btnClicked} className="answer-button">{answer}</button>
                    )
                })}
                <hr></hr>
            </>
        )
    })

    return (
        <div className="question-container">
            {questionElements}
        </div>
    )
}
