import React from "react"
import { useDispatch } from "react-redux"
import { calculate } from "./score"
import { changeColor } from "./changeColor"
import { useSelector } from "react-redux"

export default function Question(props) {

    const dispatch = useDispatch()
    const color = useSelector((state) => state.color)
    console.log(color)

    const [flag,setFlag] = React.useState(true)

    // TOGGLE BUTTON BACKGROUND COLOR WHEN BTN CLICKED
    function toggleBtnBgColor(btn, answer) {
        if(flag) {
            btn.target.classList.toggle("dark-bg-color")
            setFlag(false)

            if(btn.target.value == answer) {
                dispatch(calculate(1))
                dispatch(changeColor("green"))
            }
            dispatch(changeColor("red"))
        }
    }

    return (
        <>
            <div className="question" key={props.index}>{props.question}</div>
                {props.answers.map((answer,index)=> {
                    return (
                        <button style={{backgroundColor: color}} key={index} value={answer} onClick={event => toggleBtnBgColor(event,answer)} className="answer-button">{answer}</button>
                    )
            })}
            <hr></hr>
        </>
    )
}
