import React from "react"
import { useDispatch } from "react-redux"
import { calculate } from "./score"
import { useSelector } from "react-redux"
import { initColor } from "./color"
import Button from "./Button"

export default function Question(props) {

    const dispatch = useDispatch()
    const color = useSelector((state) => state.color.value)
    const [btnClassNames, setBtnClassNames] = React.useState("answer-button") 
    const [flag, setFlag] = React.useState(true)
    const [mutateButtons, setMutateButtons] = React.useState(false)

    React.useEffect(() => {
        setFlag(true)
    }, [props.answers])


    return (
        <>
            <div className="question" key={props.index}>{props.question}</div>
                {props.answers.map((answer,index)=> {
                    return (
                        <Button setMutateButtons={setMutateButtons} key={index} answer={answer} value={answer} correctAnswer={answer.correctAnswer} />
                    )
            })}
        </>
    )
}
