import React from "react"
import Question from "./Question"
import { useSelector } from "react-redux"

export default function Quiz() {

    const score = useSelector((state) => state.score.value)

    const [data, setData] = React.useState([])
    const [startAgain, setStartAgain] = React.useState(false)
    const [restart, setRestart] = React.useState(false)

    // FETCH QUIZ DATA FROM API
    const fetchData = async () => {
        await fetch("https://opentdb.com/api.php?amount=5&type=multiple")
        .then((response) => response.json())
        .then((data) => setData(data.results))
    }

    React.useEffect(()=> {
        setRestart(false)
        fetchData()
    },[restart])


    // SHUFFLE ALGORITHM TO USE LATER
    function shuffle(array) {
        let currentIndex = array.length,  randomIndex;

        // While there remain elements to shuffle.
        while (currentIndex != 0) {
          // Pick a remaining element.
          randomIndex = Math.floor(Math.random() * currentIndex);
          currentIndex--;
          // And swap it with the current element.
          [array[currentIndex], array[randomIndex]] = [
            array[randomIndex], array[currentIndex]];
        }
        return array;
    }

    // RENDER QUESTIONS
    const questionElements = data.map((question, index) => {
        // ANSWERS ARRAY AND SHUFFLE
        let answers = [...question.incorrect_answers,question.correct_answer]
        shuffle(answers)

        return (
            <Question key={index} index={index} question={question.question} answers={answers}/>
        )
    })

    // CALCULATE FINAL SCORE WHEN CHECK BTN CLICKED
    function calcScore() {
        setStartAgain(true)

    }

    function resQuiz() {
        setStartAgain(false)
        setRestart(true)
    }

    return (
        <div className="question-container">
            {questionElements}
            <p>{startAgain && ("Your score is "+ score +"/5")}</p>
            {!startAgain && <button onClick={calcScore} className="check-button">Check Answers</button>}
            {startAgain && <button onClick={resQuiz} className="check-button">Restart Quiz</button>}
        </div>
    )

}
