import React from "react"
import { useDispatch } from "react-redux"
import { calculate } from "./score"

export default function Question(props) {

    const dispatch = useDispatch()

    const [flag,setFlag] = React.useState(true)

    // TOGGLE BUTTON BACKGROUND COLOR WHEN BTN CLICKED
    function toggleBtnBgColor(btn, answer) {
        if(flag) {
            btn.target.classList.toggle("dark-bg-color")
            setFlag(false)

            if(btn.target.value == answer) {
                console.log(btn.target.value, answer)
                dispatch(calculate(1))
                return
            }
        }
    }

    return (
        <>
            <div className="question" key={props.index}>{props.question}</div>
                {props.answers.map((answer,index)=> {
                    return (
                        <button key={index} value={answer} onClick={event => toggleBtnBgColor(event,props.question.correct_answer)} className="answer-button">{answer}</button>
                    )
            })}
            <hr></hr>
        </>
    )
}
