import React from "react"
import { useDispatch } from "react-redux"
import { calculate } from "./score"
import { useSelector } from "react-redux"
import { initColor } from "./color"

export default function Question(props) {

    const dispatch = useDispatch()
    const color = useSelector((state) => state.color.value)

    const [flag,setFlag] = React.useState(true)

    React.useEffect(() => {},[color])

    // TOGGLE BUTTON BACKGROUND COLOR WHEN BTN CLICKED
    function toggleBtnBgColor(btn, correctAnswer) {
        if(flag) {
            if(color == "") {
                btn.target.classList.add(color)
            } else {
                btn.target.classList.remove(color)
            }

            setFlag(false)

            if(btn.target.value == correctAnswer) {
                dispatch(calculate(1))
                return
            }
        }
    }



    return (
        <>
            <div className="question" key={props.index}>{props.question}</div>
                {props.answers.map((answer,index)=> {
                    return (
                        <button key={index} value={answer} onClick={event => toggleBtnBgColor(event,props.correctAnswer)} className="answer-button">{answer}</button>
                    )
            })}
            <hr></hr>
        </>
    )
}
